def normed(clust):
	summation = max(clust)
	for a in range(len(clust)):
		clust[a] = clust[a]/summation
	return clust

def cumulative(clust):
	clustr = np.cumsum(clust)
	maxi = max(clustr)
	for k in range(len(clustr)):
		clustr[k] = clustr[k]/maxi

	return clustr

def conflevel(alpha,perc):
	alpha = cumulative(alpha)
	p = 0
	while alpha[p] < perc:
		p = p+1
	target = perc - alpha[p-1]
	m = (alpha[p]-alpha[p-1])/(x[p]-x[p-1])
	return (target/m) + x[p-1]

